import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container'
import Paper from '@mui/material/Paper'
import Button from '@mui/material/Button'
import { useState, useEffect } from 'react';

export default function SearchCourse() {
  const paperStyle = {padding:'50px 20px', width:600, margin:'20px auto'}
  const [course,setCourse] = useState()

  const handleClick=(e)=>{
    e.preventDefault()
    const course={id}
    console.log(course)
    // TODO: send data to database
    fetch("http://localhost:8080/api/v1/course", 
    {
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify(course)
    }).then(()=>{
      console.log("Course Removed")
      setIsSubmitted(true);
      setIsError(false);
    }).catch(()=>{
      console.log("Error")
      setIsError(true);
      setIsSubmitted(false);
    })
  }

  useEffect(()=>{
    fetch("http://localhost:8080/api/v1/course")
    .then(res=>res.json())
    .then(result=>{
      setCourse(result);
    })
  })

  return (
    <Container>
      <Paper elevation={3} style={paperStyle}>
        <h1>Search Course</h1>
        <p>Search a course from the database</p>

    <Box
      component="form"
      sx={{
        '& > :not(style)': { m: 1, width: 500, maxWidth: '100%' },
      }}
      noValidate
      autoComplete="off"
    >
      <TextField required id="outlined-required" label="Course ID (e.g., ENSF608)" variant="outlined" fullWidth
      value = {id}
      onChange={(e)=>setId(e.target.value)}
      />

      <Button variant="contained" onClick={handleClick}>
        Submit
      </Button>
    </Box>
    </Paper>

      <Paper elevation={3} style={paperStyle}>
      <h1>Course</h1>
        {course.map(c=>(
          <Paper elevation={6} style={{margin:"10px",padding:"15px",textAlign:"left"}} key={c.id}>
            ID:{c.id},
            Name:{c.name},
            Capacity:{c.capacity}
          </Paper>
        ))}
      </Paper>

    </Container>
  );
}
